var request = require('request');
var base = 'http://localhost:8777';
var fs = require('fs-extra');

fs.emptyDir(__dirname + '/output', function(err){

  if (err) throw err;

  request(base + '/writewrite', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });

  request(base + '/writewriteend', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });

  request(base + '/send', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });

  request(base + '/sendStatus', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });

  request(base + '/sendFile', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });

  request(base + '/404', function(err, res, body){

    if (err) throw err;

    console.log(body);
  });
});
