var fs = require('fs-extra');
var join = require('path').join;
var CACHE_DIRECTORY = require('helper').rootDir + '/www/';

module.exports =  function (hostname, callback) {

  callback = callback || function (err) {if (err) throw err;};

  fs.emptyDir(join(CACHE_DIRECTORY, hostname), function(err){

    if (err) return callback(new Error('Cache Flush Error: ' + err.message));

    callback();
  });
};
